//실습 3
public class AreaTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double PI = 3.141592;
		
		System.out.println("원의 넓이를 구하는 코드입니다.");
		int radius = 3;
		double area = radius * radius * PI;
		System.out.println("반지름이 " + radius + "인 원의 면적은 " + area + "입니다.");
	}

}
